using System;using System.Drawing;using System.Windows.Forms;using LojaConstrucao.Forms;namespace LojaConstrucao{public partial class MainForm:Form{public MainForm(){InitializeComponent();this.StartPosition=FormStartPosition.CenterScreen;}private void btnProdutos_Click(object sender,EventArgs e){var form=new ProdutoForm();form.ShowDialog();}private void btnClientes_Click(object sender,EventArgs e){var form=new ClienteForm();form.ShowDialog();}private void btnVendas_Click(object sender,EventArgs e){var form=new VendaForm();form.ShowDialog();}private void btnSair_Click(object sender,EventArgs e){if(MessageBox.Show("Deseja realmente sair do sistema?","Confirmacao",MessageBoxButtons.YesNo,MessageBoxIcon.Question)==DialogResult.Yes){Application.Exit();}}}partial class MainForm{private Button btnProdutos,btnClientes,btnVendas,btnSair;private Label lblTitulo,lblVersao;private void InitializeComponent(){this.btnProdutos=new Button();this.btnClientes=new Button();this.btnVendas=new Button();this.btnSair=new Button();this.lblTitulo=new Label();this.lblVersao=new Label();this.lblTitulo.AutoSize=true;this.lblTitulo.Font=new Font("Microsoft Sans Serif",18F,FontStyle.Bold);this.lblTitulo.ForeColor=Color.DarkBlue;this.lblTitulo.Location=new Point(50,30);this.lblTitulo.Text="Sistema Loja Construcao";this.lblVersao.AutoSize=true;this.lblVersao.ForeColor=Color.Gray;this.lblVersao.Location=new Point(50,65);this.lblVersao.Text="Versao 3.0 - Sistema Completo";this.btnProdutos.BackColor=Color.DarkBlue;this.btnProdutos.ForeColor=Color.White;this.btnProdutos.Location=new Point(50,120);this.btnProdutos.Size=new Size(150,50);this.btnProdutos.Text="Produtos";this.btnProdutos.Click+=new EventHandler(this.btnProdutos_Click);this.btnClientes.BackColor=Color.DarkGreen;this.btnClientes.ForeColor=Color.White;this.btnClientes.Location=new Point(220,120);this.btnClientes.Size=new Size(150,50);this.btnClientes.Text="Clientes";this.btnClientes.Click+=new EventHandler(this.btnClientes_Click);this.btnVendas.BackColor=Color.DarkOrange;this.btnVendas.ForeColor=Color.White;this.btnVendas.Location=new Point(390,120);this.btnVendas.Size=new Size(150,50);this.btnVendas.Text="Vendas";this.btnVendas.Click+=new EventHandler(this.btnVendas_Click);this.btnSair.BackColor=Color.DarkRed;this.btnSair.ForeColor=Color.White;this.btnSair.Location=new Point(490,20);this.btnSair.Size=new Size(80,30);this.btnSair.Text="Sair";this.btnSair.Click+=new EventHandler(this.btnSair_Click);this.ClientSize=new Size(600,300);this.Controls.Add(this.lblTitulo);this.Controls.Add(this.lblVersao);this.Controls.Add(this.btnProdutos);this.Controls.Add(this.btnClientes);this.Controls.Add(this.btnVendas);this.Controls.Add(this.btnSair);this.Text="Sistema Loja Construcao - Menu Principal";this.BackColor=Color.WhiteSmoke;}}}