using System;using System.IO;namespace LojaConstrucao.Utils{public static class Logger{private static readonly string LogPath="logs";private static readonly string ErrorLog=Path.Combine(LogPath,"error.log");private static readonly string SecurityLog=Path.Combine(LogPath,"security.log");static Logger(){try{if(!Directory.Exists(LogPath))Directory.CreateDirectory(LogPath);}catch{}}public static void LogError(string msg,Exception ex=null){try{var entry=$"{DateTime.Now:yyyy-MM-dd HH:mm:ss} [ERROR] {msg}";if(ex!=null)entry+=$"\nException: {ex}";File.AppendAllText(ErrorLog,entry+Environment.NewLine);}catch{}}public static void LogSecurity(string action,string user,bool success){try{var status=success?"SUCCESS":"FAILED";var entry=$"{DateTime.Now:yyyy-MM-dd HH:mm:ss} [SECURITY] {action} - User: {user} - Status: {status}";File.AppendAllText(SecurityLog,entry+Environment.NewLine);}catch{}}public static void LogInfo(string msg){try{var entry=$"{DateTime.Now:yyyy-MM-dd HH:mm:ss} [INFO] {msg}";File.AppendAllText(ErrorLog,entry+Environment.NewLine);}catch{}}}}