using System;namespace LojaConstrucao.Utils{public static class ValidationHelper{public static bool IsValidString(string input,int minLength=1,int maxLength=100){return!string.IsNullOrWhiteSpace(input)&&input.Trim().Length>=minLength&&input.Trim().Length<=maxLength;}public static bool IsValidPassword(string password){return!string.IsNullOrWhiteSpace(password)&&password.Length>=4;}public static bool IsValidDecimal(string input,out decimal value){value=0;return decimal.TryParse(input,out value)&&value>=0;}public static bool IsValidInteger(string input,out int value,int min=0){value=0;return int.TryParse(input,out value)&&value>=min;}public static string SanitizeInput(string input){return string.IsNullOrWhiteSpace(input)?string.Empty:input.Trim().Replace("'","''");}public static bool IsValidEmail(string email){if(string.IsNullOrWhiteSpace(email))return false;try{var addr=new System.Net.Mail.MailAddress(email);return addr.Address==email;}catch{return false;}}public static bool IsValidPhone(string phone){if(string.IsNullOrWhiteSpace(phone))return false;var cleanPhone=System.Text.RegularExpressions.Regex.Replace(phone,@"[^0-9]","");return cleanPhone.Length>=10&&cleanPhone.Length<=11;}}}